import React, { useState, useEffect } from 'react';

function ProductForm({ onSave, productToEdit }) {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [creationDate, setCreationDate] = useState('');

  useEffect(() => {
    if (productToEdit) {
      setName(productToEdit.name);
      setPrice(productToEdit.price);
      setCategory(productToEdit.category);
      setCreationDate(productToEdit.creationDate);
    }
  }, [productToEdit]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      name,
      price,
      category,
      creationDate: creationDate || new Date().toISOString(),
    });
    setName('');
    setPrice('');
    setCategory('');
    setCreationDate('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{productToEdit ? 'Edit Product' : 'Create New Product'}</h2>
      <div>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Price:</label>
        <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
      </div>
      <div>
        <label>Category:</label>
        <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} required />
      </div>
      <div>
        <label>Creation Date:</label>
        <input type="date" value={creationDate} onChange={(e) => setCreationDate(e.target.value)} />
      </div>
      <button type="submit">{productToEdit ? 'Update Product' : 'Create Product'}</button>
    </form>
  );
}

export default ProductForm;
