import React, { useCallback, useState, useEffect } from 'react';
import {
  getAllProducts,
  getAllCategories,
  createNewProduct,
  updateProduct,
  deleteProduct,
} from './api';
import ProductList from './components/ProductList';
import Pagination from './components/Pagination';
import CategoryFilter from './components/CategoryFilter';
import ProductForm from './components/ProductForm';
import './App.css';

function App() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [sortOption, setSortOption] = useState('name'); // Default sort by name
  const [priceRange, setPriceRange] = useState({ min: 0, max: Infinity }); // Default price range

  // Memoizar la función fetchProducts para evitar ciclos de renderizado innecesarios
  const fetchProducts = useCallback(async (page, category, priceRange) => {
    try {
      let data = await getAllProducts(page);
      if (category) {
        data.data = data.data.filter((product) => product.category === category);
      }

      // Filtrar por rango de precios
      data.data = data.data.filter((product) => product.price >= priceRange.min && product.price <= priceRange.max);

      // Ordenar los productos según la opción seleccionada
      if (sortOption === 'name') {
        data.data.sort((a, b) => a.name.localeCompare(b.name));
      } else if (sortOption === 'price') {
        data.data.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
      } else if (sortOption === 'date') {
        data.data.sort((a, b) => new Date(b.creationDate) - new Date(a.creationDate)); // Más nuevo primero
      } else if (sortOption === 'category') {
        data.data.sort((a, b) => a.category.localeCompare(b.category));
      }

      setProducts(data.data);
      setTotalPages(data.total_pages);
    } catch (error) {
      console.error(error);
    }
  }, [sortOption]);

  useEffect(() => {
    fetchProducts(currentPage, selectedCategory, priceRange);
    fetchCategories();
  }, [currentPage, selectedCategory, sortOption, priceRange, fetchProducts]);

  const fetchCategories = async () => {
    try {
      const data = await getAllCategories();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const handlePriceRangeChange = (min, max) => {
    setPriceRange({ min, max });
  };

  const handleSaveProduct = async (product) => {
    try {
      if (selectedProduct) {
        await updateProduct(selectedProduct.id, product);
      } else {
        await createNewProduct(product);
      }
      fetchProducts(currentPage, selectedCategory, priceRange);
      setSelectedProduct(null);
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    setSelectedProduct(product);
  };

  return (
    <div className="container">
      <h1>Products</h1>

      <ProductForm onSave={handleSaveProduct} productToEdit={selectedProduct} />

      <CategoryFilter
        categories={categories}
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
      />

      <div className="sort">
        <label htmlFor="sort">Sort by:</label>
        <select id="sort" value={sortOption} onChange={handleSortChange}>
          <option value="name">Name</option>
          <option value="price">Price</option>
          <option value="date">Creation Date</option>
          <option value="category">Category</option>
        </select>
      </div>

      <div className="filter">
        <label htmlFor="minPrice">Min Price:</label>
        <input type="number" id="minPrice" onChange={(e) => handlePriceRangeChange(e.target.value, priceRange.max)} />
        <label htmlFor="maxPrice">Max Price:</label>
        <input type="number" id="maxPrice" onChange={(e) => handlePriceRangeChange(priceRange.min, e.target.value)} />
      </div>

      <ProductList
        products={products}
        onView={handleEditProduct}
        onDelete={deleteProduct}
      />

      <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={setCurrentPage}
      />
    </div>
  );
}

export default App;
