import { useState } from "react";

const BuscarSerie = () => {
    const [serie, setSerie] = useState(null);
    const [error, setError] = useState(null);
    const [id, setId] = useState("");

    const buscarSeriePorID = () => {
        fetch(`https://peticiones.online/api/series/${id}`)
            .then(response => response.json())
            .then(json => {
                if (json.error) {
                    setError(json.error);
                    setSerie(null);
                } else {
                    setSerie(json);
                    setError(null);
                }
            })
            .catch(err => setError("Error al recuperar la serie"));
    };

    return (
        <div className="buscar-serie">
            <h2>Buscar Serie por ID</h2>
            <input 
                type="text" 
                placeholder="Introduce el ID de la serie" 
                value={id} 
                onChange={e => setId(e.target.value)} 
            />
            <button onClick={buscarSeriePorID}>Buscar</button>

            {error && <div className="error">{error}</div>}
            {serie && (
                <div className="serie">
                    <h2>{serie.title}</h2>
                    <p>Creador: {serie.creator}</p>
                    <img src={serie.image} alt={serie.title} />
                    <p>Puntuaci√≥n: {serie.rating}</p>
                    <p>Canal: {serie.channel}</p>
                </div>
            )}
        </div>
    );
}

export default BuscarSerie;
