import React, { useState, useEffect } from 'react';
import {
  getAllProducts,
  getProductById,
  getAllCategories, // This imports the function from the API file
  createNewProduct,
  updateProduct,
  deleteProduct,
} from './api';
import ProductList from './components/ProductList';
import Pagination from './components/Pagination';
import CategoryFilter from './components/CategoryFilter';
import './App.css';

function App() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [sortOption, setSortOption] = useState('name'); // Default sort by name

  useEffect(() => {
    fetchProducts(currentPage, selectedCategory);
    fetchCategories(); // Here is where it's called
  }, [currentPage, selectedCategory, sortOption]);

  const fetchProducts = async (page, category) => {
    try {
      let data = await getAllProducts(page);
      if (category) {
        data.data = data.data.filter((product) => product.category === category);
      }

      // Sort the products based on the selected option
      if (sortOption === 'name') {
        data.data.sort((a, b) => a.name.localeCompare(b.name));
      } else if (sortOption === 'price') {
        data.data.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
      }

      setProducts(data.data);
      setTotalPages(data.total_pages);
    } catch (error) {
      console.error(error);
    }
  };

  // Define the fetchCategories function
  const fetchCategories = async () => {
    try {
      const data = await getAllCategories();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  return (
    <div className="container">
      <h1>Products</h1>

      <CategoryFilter
        categories={categories}
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
      />

      <div className="sort">
        <label htmlFor="sort">Sort by:</label>
        <select id="sort" value={sortOption} onChange={handleSortChange}>
          <option value="name">Name</option>
          <option value="price">Price</option>
        </select>
      </div>

      <ProductList
        products={products}
        onView={setSelectedProduct}
        onDelete={deleteProduct}
      />

      <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={setCurrentPage}
      />
    </div>
  );
}

export default App;
